content.text:
# Adicionando as entradas BibTeX para as legislações
bibtex_entries += """
@misc{Brasil2018LGPD,
  title        = {{Lei nº 13.709, de 14 de agosto de 2018. Lei Geral de Proteção de Dados Pessoais (LGPD)}},
  year         = {2018},
  howpublished = {Diário Oficial da União},
  url          = {http://www.planalto.gov.br/ccivil_03/_Ato2015-2018/2018/Lei/L13709.htm}
}

@misc{EU2016GDPR,
  title        = {{Regulamento (UE) 2016/679 do Parlamento Europeu e do Conselho de 27 de abril de 2016}},
  year         = {2016},
  howpublished = {Jornal Oficial da União Europeia},
  url          = {https://eur-lex.europa.eu/legal-content/PT/TXT/?uri=celex%3A32016R0679}
}

@misc{USA1996HIPAA,
  title        = {{Health Insurance Portability and Accountability Act of 1996}},
  year         = {1996},
  howpublished = {U.S. Department of Health & Human Services},
  url          = {https://www.hhs.gov/hipaa/for-professionals/privacy/laws-regulations/index.html}
}

@misc{California2018CCPA,
  title        = {{Assembly Bill No. 375, Chapter 55, California Consumer Privacy Act of 2018}},
  year         = {2018},
  howpublished = {California Legislative Information},
  url          = {https://leginfo.legislature.ca.gov/faces/billTextClient.xhtml?bill_id=201720180AB375}
}
"""

# Atualizando o arquivo BibTeX
with open(file_path, "w") as file:
    file.write(bibtex_entries)

file_path

